@onstart()
def my_onstart(self):
    self.drawing = True

@onstart(when = 'clone')
def my_onstartclone(self):
    self.drawing = True
    r = random.randrange(256)
    g = random.randrange(256)
    b = random.randrange(256)
    self.pen_color = (r, g, b)
    self.heading = random.random() * 360
    self.forward(random.random() * 30 + 20)

@onclick()
def my_onclick(self, x, y):
    self.clone()

@onkey('up arrow')
def my_onup(self):
    self.forward(10)

@onkey('down arrow')
def my_ondown(self):
    self.forward(-10)

@onkey('left arrow')
def my_onleft(self):
    self.turn_left(15)

@onkey('right arrow')
def my_onright(self):
    self.turn_right(15)
